import React from 'react';
import { Outlet } from 'react-router-dom';
import { Container, IconButton, Drawer, List, ListItem, ListItemText, Box } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

const MainLayout = () => {
    const [drawerOpen, setDrawerOpen] = React.useState(false);

    const toggleDrawer = (open) => () => {
        setDrawerOpen(open);
    };

    return (
        <Box sx={{ display: 'flex' }}>
            {/* Гамбургерная иконка */}
            {!drawerOpen && (
                <IconButton
                    onClick={toggleDrawer(true)}
                    sx={{ position: 'absolute', top: 16, left: 16, zIndex: 1300 }}
                >
                    <MenuIcon />
                </IconButton>
            )}

            {/* Боковое меню */}
            <Drawer
                anchor='left'
                open={drawerOpen}
                onClose={toggleDrawer(false)}
                sx={{ width: '50%', flexShrink: 0 }}
            >
                <Box
                    sx={{ width: 250 }}
                    role="presentation"
                    onClick={toggleDrawer(false)}
                    onKeyDown={toggleDrawer(false)}
                >
                    <List>
                        <ListItem button>
                            <ListItemText primary="Load New Data" />
                        </ListItem>
                        <ListItem button>
                            <ListItemText primary="Database" />
                        </ListItem>
                        <ListItem button>
                            <ListItemText primary="Settings" />
                        </ListItem>
                    </List>
                </Box>
            </Drawer>

            {/* Контент страницы */}
            <Container maxWidth="md" sx={{ mt: 4, flexGrow: 1, ml: drawerOpen ? '250px' : 0 }}>
                {/* Вставляем маршруты */}
                <Outlet />
            </Container>
        </Box>
    );
};

export default MainLayout;
