// pages/Home.jsx
import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box, Card, CardContent } from '@mui/material';

const Home = () => {
    const [message, setMessage] = useState('');     // Для хранения введенного сообщения
    const [response, setResponse] = useState('');   // Для хранения ответа

    // Функция для обработки нажатия кнопки
    const handleSendMessage = () => {
        // Симулируем получение ответа
        const simulatedResponse = `You asked: "${message}". Here is a simulated AI response.`;
        setResponse(simulatedResponse);
    };

    return (
        <Container maxWidth="md" sx={{ mt: 4 }}>
            <Typography variant="h4" gutterBottom align="center" color="primary">
                AI Communication
            </Typography>

            <Box sx={{ mt: 4, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                {/* Поле ввода сообщения */}
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Enter your question"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    sx={{ mb: 2 }}
                />
                {/* Кнопка отправки */}
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSendMessage}
                    sx={{ width: '50%' }}
                >
                    Send
                </Button>
            </Box>

            {/* Отображение ответа, если он есть */}
            {response && (
                <Card sx={{ mt: 4, backgroundColor: 'secondary.main', color: 'primary.main' }}>
                    <CardContent>
                        <Typography variant="h6">AI Response:</Typography>
                        <Typography variant="body1">{response}</Typography>
                    </CardContent>
                </Card>
            )}
        </Container>
    );
};

export default Home;
