import axios from 'axios';

// Set up the base URL for your FastAPI backend
const API_URL = 'http://localhost:8081'; // Update this URL based on your FastAPI server configuration

// Function to ask a question
export async function askQuestion(question, imagePath) {
  try {
    const response = await axios.post(`${API_URL}/ask/`, {
      question,
      image_path: imagePath || ''
    }, {
      headers: {
        'Content-Type': 'application/json'
      }
    });

    // Check if response structure is as expected
    const responseData = response.data.response;

    let content;
    let logs;

    if (typeof responseData === 'string') {
      // Handle cases where responseData is a simple string
      content = responseData;
      logs = {}; // Or any default value
    } else {
      // Handle cases where responseData is an object with more details
      content = responseData.content || 'No content available';
      logs = responseData.logs || {};
    }

    return { content, logs };
  } catch (error) {
    console.error("Error asking question:", error);
    throw error;
  }
}

// Function to upload a file
export async function uploadFile(file) {
  try {
    const formData = new FormData();
    formData.append('file', file);

    const response = await axios.post(`${API_URL}/upload/`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    });

    return response.data.filename;
  } catch (error) {
    console.error("Error uploading file:", error);
    throw error;
  }
}

// Function to load data from a given URL

// Function to load data from URL
export async function loadData(url) {
  try {
    const response = await axios.post(`${API_URL}/load_data/`, null, {
      params: { url },
      headers: {
        'Content-Type': 'application/json'
      }
    });

    return response.data;
  } catch (error) {
    console.error("Error loading data:", error);
    throw error;
  }
}
