import React, { useState, useRef, useEffect } from 'react';
import { Container, TextField, Button, Typography, Box, IconButton, Menu, MenuItem } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import LanguageIcon from '@mui/icons-material/Language';

const Home = () => {
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [logs, setLogs] = useState([]);
    const [showLogs, setShowLogs] = useState(false);
    const [language, setLanguage] = useState('EN');
    const [anchorEl, setAnchorEl] = useState(null);
    const logsEndRef = useRef(null);

    // Симуляция отправки сообщения
    const handleSendMessage = () => {
        if (message.trim() === '') return;

        // Добавляем сообщение пользователя в историю чата
        setChatHistory((prevHistory) => [
            ...prevHistory,
            { sender: 'user', text: message }
        ]);

        // Симулируем ответ ИИ
        const simulatedResponse = `Ответ ИИ на: "${message}"`;
        setChatHistory((prevHistory) => [
            ...prevHistory,
            { sender: 'ai', text: simulatedResponse }
        ]);

        // Очищаем поле ввода после отправки
        setMessage('');

        // Добавляем логи
        setLogs((prevLogs) => [
            ...prevLogs,
            `Message sent: ${message}`,
            `AI responded: ${simulatedResponse}`
        ]);
    };

    // Функция для копирования текста в буфер обмена
    const handleCopyToClipboard = (text) => {
        navigator.clipboard.writeText(text).then(() => {
            alert('Copied to clipboard!');
        });
    };

    // Скролл до конца логов при добавлении новых
    useEffect(() => {
        logsEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [logs]);

    // Функция для переключения отображения логов
    const toggleLogs = () => {
        setShowLogs((prev) => !prev);
    };

    // Функция для открытия меню выбора языка
    const handleClickLanguageMenu = (event) => {
        setAnchorEl(event.currentTarget);
    };

    // Функция для закрытия меню выбора языка
    const handleCloseLanguageMenu = () => {
        setAnchorEl(null);
    };

    // Функция для выбора языка
    const handleLanguageChange = (lang) => {
        setLanguage(lang);
        handleCloseLanguageMenu();
    };

    return (
        <Container maxWidth="md" sx={{ mt: 4, display: 'flex', flexDirection: 'column', height: '80vh' }}>
            <Typography variant="h4" gutterBottom align="center" color="primary">
                AI Chat
            </Typography>

            {/* Блок чата */}
            <Box sx={{ flexGrow: 1, overflowY: 'auto', mb: 2, p: 2, border: '1px solid #ccc', borderRadius: '4px', position: 'relative' }}>
                {/* Выпадающее меню для выбора языка */}
                <IconButton
                    onClick={handleClickLanguageMenu}
                    sx={{
                        position: 'absolute',
                        top: '8px',
                        right: '8px',
                        color: 'text.secondary',
                        zIndex: 1300, // Устанавливаем высокий z-index для меню
                    }}
                >
                    <LanguageIcon />
                </IconButton>
                <Typography
                    variant="caption"
                    sx={{
                        position: 'absolute',
                        top: '8px',
                        right: '40px',
                        color: 'text.secondary',
                        zIndex: 1300, // Устанавливаем высокий z-index для текста языка
                    }}
                >
                    {language}
                </Typography>

                {chatHistory.map((entry, index) => (
                    <Box
                        key={index}
                        sx={{
                            display: 'flex',
                            mb: 2,
                            justifyContent: entry.sender === 'user' ? 'flex-end' : 'flex-start',
                        }}
                    >
                        <Box
                            sx={{
                                maxWidth: '70%',
                                bgcolor: entry.sender === 'user' ? 'primary.main' : '#f1f1f1', // Светло-серый цвет для ИИ
                                color: entry.sender === 'user' ? 'white' : 'black',
                                p: 2,
                                borderRadius: entry.sender === 'user' ? '16px 16px 0 16px' : '16px 16px 16px 0',
                                wordBreak: 'break-word', // Разрывать слова, чтобы предотвратить выход за границы
                                whiteSpace: 'pre-wrap',  // Учитывать переносы строк
                                overflowWrap: 'break-word', // Переносить длинные слова
                                position: 'relative', // Для позиционирования иконки копирования
                            }}
                        >
                            {entry.text}

                            {entry.sender === 'ai' && (
                                <IconButton
                                    sx={{
                                        position: 'absolute',
                                        bottom: '-24px', // Сдвигаем иконку ниже
                                        left: '8px', // Отступ слева, совпадает с отступом текста
                                        color: 'text.secondary',
                                        '&:hover': {
                                            color: 'text.primary',
                                        },
                                        p: 0, // Убираем внутренние отступы
                                        width: '16px', // Уменьшаем размер иконки
                                        height: '16px',
                                    }}
                                    onClick={() => handleCopyToClipboard(entry.text)}
                                >
                                    <ContentCopyIcon fontSize="inherit" />
                                </IconButton>
                            )}
                        </Box>
                    </Box>
                ))}
            </Box>

            {/* Поле ввода и кнопка */}
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Enter your message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={(e) => {
                        if (e.key === 'Enter') handleSendMessage(); // Отправка при нажатии Enter
                    }}
                />
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSendMessage}
                    sx={{ ml: 2 }}
                    endIcon={<SendIcon />}
                >
                    Send
                </Button>
            </Box>

            {/* Кнопка для отображения/скрытия логов */}
            <IconButton
                onClick={toggleLogs}
                sx={{
                    mb: 2,
                    alignSelf: 'flex-start',
                    color: 'primary.main',
                    '&:hover': {
                        color: 'primary.dark',
                    },
                }}
            >
                {showLogs ? <ExpandLessIcon /> : <ExpandMoreIcon />}
                <Typography variant="button" sx={{ ml: 1 }}>
                    Check Logs
                </Typography>
            </IconButton>

            {/* Выпадающее окно с логами */}
            {showLogs && (
                <Box
                    sx={{
                        height: '150px', // Высота окна с логами
                        overflowY: 'auto',
                        borderTop: '1px solid #ccc',
                        p: 2,
                        bgcolor: '#f9f9f9',
                        borderRadius: '4px',
                    }}
                >
                    {logs.map((log, index) => (
                        <Typography key={index} variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                            {log}
                        </Typography>
                    ))}
                    <div ref={logsEndRef} /> {/* Элемент для автоматической прокрутки */}
                </Box>
            )}

            {/* Выпадающее меню выбора языка */}
            <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleCloseLanguageMenu}
                sx={{ 
                    mt: '100px', // Отступ сверху для размещения меню ниже кнопки
                    zIndex: 1400 // Устанавливаем высокий z-index для меню
                }}
            >
                <MenuItem selected={language === 'EN'} onClick={() => handleLanguageChange('EN')}>EN</MenuItem>
                <MenuItem selected={language === 'DE'} onClick={() => handleLanguageChange('DE')}>DE</MenuItem>
                <MenuItem selected={language === 'RU'} onClick={() => handleLanguageChange('RU')}>RU</MenuItem>
                <MenuItem selected={language === 'UA'} onClick={() => handleLanguageChange('UA')}>UA</MenuItem>
            </Menu>
        </Container>
    );
};

export default Home;
