import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const Home = () => {
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);

    // Симуляция отправки сообщения
    const handleSendMessage = () => {
        if (message.trim() === '') return;

        // Добавляем сообщение пользователя в историю чата
        setChatHistory((prevHistory) => [
            ...prevHistory,
            { sender: 'user', text: message }
        ]);

        // Симулируем ответ ИИ
        const simulatedResponse = `Ответ ИИ на: "${message}"`;
        setChatHistory((prevHistory) => [
            ...prevHistory,
            { sender: 'ai', text: simulatedResponse }
        ]);

        // Очищаем поле ввода после отправки
        setMessage('');
    };

    return (
        <Container maxWidth="md" sx={{ mt: 4, display: 'flex', flexDirection: 'column', height: '80vh' }}>
            <Typography variant="h4" gutterBottom align="center" color="primary">
                AI Chat
            </Typography>

            {/* Блок чата */}
            <Box sx={{ flexGrow: 1, overflowY: 'auto', mb: 2, p: 2, border: '1px solid #ccc', borderRadius: '4px' }}>
                {chatHistory.map((entry, index) => (
                    <Box
                        key={index}
                        sx={{
                            display: 'flex',
                            mb: 2,
                            justifyContent: entry.sender === 'user' ? 'flex-end' : 'flex-start',
                            position: 'relative', // Для позиционирования аватара
                        }}
                    >
                        {/* Сообщение */}
                        <Box
                            sx={{
                                maxWidth: '70%',
                                bgcolor: entry.sender === 'user' ? 'primary.main' : 'secondary.light',
                                color: entry.sender === 'user' ? 'white' : 'black',
                                p: 2,
                                borderRadius: entry.sender === 'user' ? '16px 16px 0 16px' : '16px 16px 16px 0',
                                display: 'flex',
                                alignItems: 'center',
                                position: 'relative', // Для внутреннего позиционирования
                            }}
                        >
                            {entry.text}
                            {/* Аватарка внутри сообщения */}
                            {/* Пользовательский аватар размещен в правом верхнем углу сообщения */}
                            {entry.sender === 'user' && (
                                <Box
                                    sx={{
                                        position: 'absolute',
                                        top: '-16px',
                                        right: '-16px',
                                        bgcolor: 'primary.dark',
                                        width: 32,
                                        height: 32,
                                        borderRadius: '50%',
                                        display: 'flex',
                                        justifyContent: 'center',
                                        alignItems: 'center',
                                        color: 'white',
                                    }}
                                >
                                    U
                                </Box>
                            )}
                            {/* ИИ аватар размещен в левом верхнем углу сообщения */}
                            {entry.sender === 'ai' && (
                                <Box
                                    sx={{
                                        position: 'absolute',
                                        top: '-16px',
                                        left: '-16px',
                                        bgcolor: 'secondary.main',
                                        width: 32,
                                        height: 32,
                                        borderRadius: '50%',
                                        display: 'flex',
                                        justifyContent: 'center',
                                        alignItems: 'center',
                                        color: 'white',
                                    }}
                                >
                                    AI
                                </Box>
                            )}
                        </Box>
                    </Box>
                ))}
            </Box>

            {/* Поле ввода и кнопка */}
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Enter your message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={(e) => {
                        if (e.key === 'Enter') handleSendMessage(); // Отправка при нажатии Enter
                    }}
                />
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSendMessage}
                    sx={{ ml: 2 }}
                    endIcon={<SendIcon />}
                >
                    Send
                </Button>
            </Box>
        </Container>
    );
};

export default Home;
